import React from "react";
import address from'../../images/images/address.png'
import { withGoogleMap, withScriptjs, GoogleMap , Marker ,InfoBox} from "react-google-maps";
const options = { closeBoxURL: '', enableEventPropagation: true };
const Map = () => {
  return (
    <div>
      <GoogleMap defaultZoom={5} defaultCenter={{ lat: 10.976827, lng: 106.674147 }}>
        <Marker
          icon={{
            url: {address},
            scaledSize: new window.google.maps.Size(40, 40),
          }}
          position={{ lat: 10.976827, lng: 106.674147 }}
        >
            <InfoBox
              options={options}
            >
              <>
                <div style={{ backgroundColor: 'green', color: 'white', borderRadius:'1em', padding: '0.2em' }}>
                  Trọ Khánh Zu
                </div>
              </>
            </InfoBox> 
          </Marker>
      </GoogleMap>
    </div>
  );
};

export default withScriptjs(withGoogleMap(Map));
