import React from "react";
import LogoGosto from "../../images/images/GOSTO.png";
import BannerBeTrai from "../../images/images/BETrai.jpg";
import BannerBeGai from "../../images/images/BEGai.jpg";
import "./Home.css";
import Header from "../../component/header/header";
import SliderHome from "../../component/SliderHome/SliderHome";
import Footer from "../../component/footer/footer";
import Announcement from "../../component/announcement/announcement";
import Banner from "../../component/banner/banner";
import ProductsHome from "../../component/ProductsHome/ProductsHome";
import HomeBanner from "../../component/banner/HomeBanner/HomeBanner";
import image from "../../images/Hunter-Nam/02_fb96605aaf6941a19ca8a037ad2c769a_medium.jpg";
import Product from "../../component/Product/Product";
//   Đổ dữ liệu dang foreach (éo thằng nào làm có tâm như tao)
var array = [];
for (let i = 0; i <= 16; i++) {
  array.push(
    <Product
      image={image}
      TotalSize={7}
      valueSize_1={39}
      valueSize_2={39}
      valueSize_3={39}
      valueSize_4={39}
      valueSize_5={39}
      name="Giày Thể Thao Nam Biti’s Hunter X Z Collection InGreenZ DSMH06300REU"
      type="Hunter Nam"
      prime={699000}
    />
  );
}
//
export default function Home() {
  return (
    <>
      <Header />
      <SliderHome />
      <Announcement />
      <Banner />
      {/* name là tên của loại sản phẩm , url là đường dẫn trang ,datalist là mảng chứa dữ liệu, visible and color là thuộc tính css riêng biệt của từng page */}
      <ProductsHome name="NAM" url="#" visible="none" datalist={array} />
      <ProductsHome name="NỮ" url="#" visible="none" datalist={array} />
      <ProductsHome name="PHỤ KIỆN" url="#" visible="none" datalist={array} />
      <ProductsHome logo={LogoGosto} url="#" color="white" datalist={array} />
      <HomeBanner banner={BannerBeTrai} />
      <ProductsHome name="BÉ TRAI" url="#" visible="none" datalist={array} />
      <HomeBanner banner={BannerBeGai} />
      <ProductsHome name="BÉ GÁI" url="#" visible="none" datalist={array} />
      <Footer />
    </>
  );
}
