import Product from "../Product/Product";
import Prev from'../../images/images/arrow-next.webp'
import "./ProductsHome.css";
import React, { useState, useEffect } from "react";


export default function ProductsHome(props) {
    let b = 0;
    const [transform , settransform] = useState(`translate3d(${b}px, 0px, 0px)`);
    function arrow_left(){
        settransform (`translate3d(${b + 228}px, 0px, 0px)`);
        b+=228;
    }
    function arrow_right(){
        settransform (`translate3d(${b - 228}px, 0px, 0px)`);
        b-=228;
    }
  return (
    <section className="home_cate">
      <div className="container">
        <div className="row">
          <div className="home_cate-box-title">
            <h2 style={{background:props.color}}>
              <a href="#">{props.name}</a>
              <img src={props.logo} alt="logo" style={{display:props.visible}}/>
            </h2>
          </div>        
          <div className="home_cate-products">
            <div className="home_cate-products-content">
              <div className="home_cate-products-wrapper-outer">
                <div className="home_cate-product-wrapper" style={{transform : transform}}>
                    {/* Đổ sản phẩm */}
                  <Product />
                  <Product />
                  <Product />
                  <Product />
                  <Product />
                  <Product />
                  <Product />
                  <Product />
                  <Product />
                  <Product />
                  <Product />
                  <Product />
                  <Product />
                  <Product />
                  <Product />
                  <Product />
                  {/* </ Đổ sản phẩm > */}
                </div>
              </div>
            </div>
            <div className="arrow_product-slider">
                <a className="btn_prev" style={{backgroundImage:`url(${Prev})`}} onClick={arrow_left}></a>
                <a className="btn_next"  style={{backgroundImage:`url(${Prev})`}} onClick={arrow_right}></a>
            </div>
          </div>
        <div className="home_cate-link">
            <a href={props.url} className="home_cate-link-text">XEM THÊM</a>
        </div>
        </div>
      </div>
    </section>
  );
}
