                    <div className="Cart_TotalPrime">
                      <span className="Cart_TotalPrime-title">Tá»ng Tiá»n:</span>
                      <span className="Cart_TotalPrime-prime">0 Ä</span>
                    </div>
                    <div className="Cart_button">
                      <a href={props.cart} className="Cart_button-watch">
                        XEM GIá» HÃNG
                      </a>
                      <a href={props.payment} className="Cart_button-pay">
                        THANH TOÃN
                      </a>
                    </div>
                  </div>
                  {/* ÄÃ³ng Tháº» cart áº©n */}
                </span>
              </div>
              {/* header_bottom-icon */}
            </div>
            {/* header_bottom-wrapper */}
          </div>
          {/* </row> */}
        </div>
        {/* </container> */}
      </div>
      {/* </header_bottom> */}
    </header>
  );
}
